#pragma once

#include "algorithms/sales-by-match.h"
#include "algorithms/abbreviation.h"

#include <iostream>
#include <vector>
#include <algorithm>
#include <map>

namespace hackerrank {
    class test {
    public:
        test() = delete;
        test(const test& other) = delete;
        test& operator = (const test& other) = delete;
        ~test() = delete;

    static void run() {
        test_sales_by_match();
        test_abbreviation();
    }

    private:

        static void test_sales_by_match() {
            std::cout << "--- Sales By Match ---" << std::endl;

            std::map<std::pair<std::vector<int>, int>, int> test_cases = {
                { { { 10, 20, 20, 10, 10, 30, 50, 10, 20 }, 9}, 3 }
            };

            sales_by_match test;
            std::for_each(std::begin(test_cases), std::end(test_cases), [&test](auto i) {
                auto result = test.sock_merchant_bitset(i.first.second, i.first.first);
                std::cout << "Pairs (std::bitset) - " << (result == i.second ? "[OK]" : "[FAIL]") << std::endl;

                result = test.sock_merchant_set(i.first.second, i.first.first);
                std::cout << "Pairs (std::unordered_set) - " << (result == i.second ? "[OK]" : "[FAIL]") << std::endl;
                });
        }

        static void test_abbreviation() {
            std::cout << "--- Dynamic Programming: Abbreviation ---" << std::endl;

            std::map<std::pair<std::string, std::string>, bool> test_cases = {
                {{"daBcd", "ABC"}, true },
                {{"Pi", "P"}, true },
                {{"AfPZN", "APZNC"}, false },
                {{"LDJAN", "LJJM"}, false },
                {{"UMKFW", "UMKFW"}, true },
                {{"KXzQ", "K"}, false },
                {{"LIT", "LIT"}, true },
                {{"QYCH", "QYCH"}, true },
                {{"DFIQG", "DFIQG"}, true },
                {{"sYOCa", "YOCN"}, false },
                {{"JHMWY", "HUVPW"}, false },
                {{"AbCdE", "AFE"}, false },
                {{"beFgH", "EFG"}, false },
                {{"beFgH", "EFH"}, true },
                {{"abAxxaBa", "ABA"}, true },
                {{"aB", "AB"} , true },
                {{"",
                ""},
                true}
            };

            abbreviation abbr;
            std::for_each(std::begin(test_cases), std::end(test_cases), [&abbr](auto i) {
                std::cout << helper::short_string<>(i.first.first) << " : " << helper::short_string<>(i.first.second) << " - ";
                std::cout << (abbr.task(i.first.first, i.first.second) == i.second ? "[OK]" : "[FAIL]") << std::endl;
                });
        }

    };

}